<?php require_once("../includes/session.php"); ?><?php require_once("../includes/db_connection.php"); ?><?php require_once("../includes/functions.php"); ?><?php require_once("../includes/validation_functions.php"); ?><?php// Provide a default value for $Email$Email = "";if (isset($_POST['submit'])) {  // Process the form  // validations  $required_fields = array("Email", "Password");  ValidatePresences($required_fields);  $fields_with_max_lengths = array("Email" => 50);    ValidateMaxLengths($fields_with_max_lengths);  if (empty($errors)) {    // Attempt login    $Email = $_POST["Email"];    $Password = $_POST["Password"];    echo $Email . " " . $Password;    $FoundMember = AttemptLogin($Email, $Password);    if ($FoundMember) {      // Success, mark user as logged in by setting a Session variable for id      $_SESSION["MemberId"] = $FoundMember["MemberId"];      // Store the username in a Session variable      $_SESSION["Email"] = $FoundMember["Email"];      RedirectTo("index.php");    } else {      // Failure      $_SESSION["message"] = "Email/password not found";    }  }} else {  // This is probably a GET request} // end: if (isset($_POST['submit']))?><?php $layout_context = "admin"; ?><?php include("../includes/layouts/header.php"); ?><div id="main">  <div id="navigation">    &nbsp;  </div>  <div id="page">    <?php echo message(); ?>    <?php echo FormErrors($errors); ?>    <h2>Login</h2>    <form action="login.php" method="post">      <p>Username:        <input type="text" name="Email" value="<?php echo htmlentities($Email); ?>" />      </p>      <p>Password:        <input type="password" name="Password" value="" />      </p>      <input type="submit" name="submit" value="Submit" />    </form>  </div></div><?php include("../includes/layouts/footer.php"); ?>